//Task 5- Write a program to print all odd numbers from 1-50
package Assignment1;

public class Task5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Printing all odd numbers from 1-50:");
		for(int i=1;i<=50;i++)
		{
			if(i%2==1)
			{
				System.out.print(i+" ");
			}
		}


	}

}
